package practicePatterns;

public class SquarePattern27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'E';
		for (char ch1 = 'A'; ch1 <= ch; ch1++) {
			for (char ch2 = 'A'; ch2 <= ch; ch2++) {
				System.out.print(ch2 + " ");

			}
			System.out.println();

		}

	}

}
