package practicePatterns;

public class SquarePattern28 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='E';
		for(char ch1=ch;ch1>='A';ch1--) {
			for(char ch2=ch;ch2>='A';ch2--) {
				System.out.print(ch1+" ");
			}
			System.out.println();
			
		}

	}

}
